<script type="491a83367915b3c202ef4c70-text/javascript">
    new Vue({
        el: '#comment_form',
        data: {
            is_opera_mini: false,
            settings: {"form_title":"Post your Contribution","form_textarea_description":"Type your contribution in the box below","form_button_text":"Post Contribution","list_title":"Contributions","title_css_class":"","num_comment_img_upload":3,"num_reply_img_upload":1,"default_filter":"best","no_comment_msg":"","show_form_message":true},
            type: 'classroom',
            post_id: 12271,
            token: 'QQCYxt402B3Tu7J1I1pUNoW9tHgYXbC8DkdA2OCR',
            quote: null,
            images: [],
            is_saving: false,
            notification: null
        },
        created(){
            this.notification = this.notification_obj();
            window.bus.$on('quote', (data) => {
                this.quote = data;
            });
        },
        methods: {
            create(){
                if(!IS_LOGGED_ON){
                    window.bus.$emit('show_login', '');
                    return;
                }

                if(this.is_saving) return;

                let comment = this.$refs['comment'].value;

                if(!comment && this.images.length < 1)
                {
                    this.notification.error('No comment message or image specified');
                    return;
                }

                this.is_saving = true;

                let formData = new FormData();
                formData.append('comment', comment);
                formData.append( '_token', this.token);

                if(this.quote) formData.append('qtid', this.quote.id);

                this.images.forEach((image) => {
                    formData.append('images[]', image);
                });

                fetch(`${BASE_URL}api/comments/${this.type}/${this.post_id}`, {
                    method: 'post',
                    body: formData,
                })
                    .then(res => {
                        if(res.status !== 200)
                        {
                            res.json().then(response => {
                                console.log(response);
                                this.notification.error(response.join());
                            });
                        }
                        else {
                            res.json().then(response => {
                                this.$refs['comment'].value = '';
                                $('.emojionearea-editor').html('');
                                this.images = [];
                                $('input[type=file]').val('');
                                window.bus.$emit('new_comment', response);
                                this.notification.success('Comment was successfully added.');
                            });
                        }
                    })
                    .catch(err => console.log(err))
                    .finally(() => {
                        this.is_saving = false;
                    });
            },
            onImageSelect(event, index){
                this.images[index] = event.target.files[0];
            },
            notification_obj(){
                return {
                    type: 'error',
                    message: '',
                    class_name: function(){
                        return this.type === 'success' ? 'alert-success' : 'alert-danger';
                    },
                    error: function (message) {
                        this.type = 'error';
                        this.message = message;
                    },
                    success: function (message) {
                        this.type = 'success';
                        this.message = message;
                    }
                };
            }
        }
    });
</script>
